$(function(){!function(){var e=new XMLHttpRequest;e.onreadystatechange=function(){if(4==this.readyState&&200==this.status)for(var n=JSON.parse(e.responseText),t=0;t<n.person.length;t++){var o=document.createElement("ul");o.innerHTML="osoba nr. "+(t+1),o.innerHTML+="<li>"+n.person[t].name+"</li>",o.innerHTML+="<li>"+n.person[t].age+"</li>",document.body.appendChild(o)}},e.open("GET","text.json",!0),e.send()}()});